import { useState } from "react";

const Form = ({createTodo}) => {
    const [enteredTodo, setEnteredTodo] = useState("");

    const addTodo = () => {
        const newTodo = {
            id: Math.random() * 1e5,
            content: enteredTodo,
        };
        createTodo(newTodo);
    }

    return (
    <div>
        <input type="text" onChange={(e) => setEnteredTodo(e.target.value)} value={enteredTodo}/>
        <button onClick={addTodo}>追加</button>
    </div>
    );
}

export default Form;
