import { useState } from "react";

const Form = ({createTodo}) => {
    const [enteredTodo, setEnteredTodo] = useState("");

    const addTodo = (e) => {
        e.p
        const newTodo = {
            id: Math.random() * 1e5,
            content: enteredTodo,
        };
        createTodo(newTodo);
        setEnteredTodo("");
    }

    return (
    <div>
        <form onScroll={addTodo}>
            <input type="text" onChange={(e) => setEnteredTodo(e.target.value)} value={enteredTodo}/>
            <button>追加</button>
        </form>
    </div>
    );
}

export default Form;
