import { useState } from "react";
import "./Example.css";

const Example = () => {
  const [theme, setTheme] = useState('light')

  const changeTheme = (e) => setTheme(e.target.value)

  return (
    <>
      <></>
      <main className={`content-${theme}`}>
        <h1>テーマの切り替え</h1>
      </main>
    </>
  );
};

export default Example;
